              <div className="space-y-2">
                <Label htmlFor="kontakt_telefon">Telefon</Label>
                <Input
                  id="kontakt_telefon"
                  type="tel"
                  value={formData.kontakt_telefon}
                  onChange={(e) => setFormData((prev) => ({ ...prev, kontakt_telefon: e.target.value }))}
                />
              </div>
            </div>
